        $scrollPane.css("height", self.$el.outerHeight(true));
        $scrollPane.css("max-height", self.$el.outerHeight(true) - 60);

        $scrollPane.jScrollPane({ showArrows: true, animateScroll: true, animateDuration: 150 });
